import React from 'react';
import { Button, Grid } from '@material-ui/core';
import ProductService from '../../services/Product';
import { withRouter } from 'react-router-dom';
import userService from '../../services/UserService';
import { FaShopify } from 'react-icons/fa';
import ReactStars from "react-rating-stars-component";
import $ from "jquery"
import "../../index.css"
const SingleProduct = (props) => {

    $(document).ready(function(){
  
        /* 1. Visualizing things on Hover - See next part for action on click */
        $('#stars li').on('mouseover', function(){
          var onStar = parseInt($(this).data('value'), 10); // The star currently mouse on
         
          // Now highlight all the stars that's not after the current hovered star
          $(this).parent().children('li.star').each(function(e){
            if (e < onStar) {
              $(this).addClass('hover');
            }
            else {
              $(this).removeClass('hover');
            }
          });
          
        }).on('mouseout', function(){
          $(this).parent().children('li.star').each(function(e){
            $(this).removeClass('hover');
          });
        });
        
        
        /* 2. Action to perform on click */
        $('#stars li').on('click', function(){
          var onStar = parseInt($(this).data('value'), 10); // The star currently selected
          var stars = $(this).parent().children('li.star');
          
          for (var i = 0; i < stars.length; i++) {
            $(stars[i]).removeClass('selected');
          }
          
          for (var i = 0; i < onStar; i++) {
            $(stars[i]).addClass('selected');
          }
          
          // JUST RESPONSE (Not needed)
          var ratingValue = parseInt($('#stars li.selected').last().data('value'), 10);
          var msg = "";
          if (ratingValue > 1) {
              msg = "Thanks! You rated this " + ratingValue + " stars.";
          }
          else {
              msg = "We will improve ourselves. You rated this " + ratingValue + " stars.";
          }
          responseMessage(msg);
          
        });
        
        
      });

      
      function responseMessage(msg) {
        $('.success-box').fadeIn(200);  
        $('.success-box div.text-message').html("<span>" + msg + "</span>");
      }
    const {product,onDelete,history}=props;
    const [count,setCount] = React.useState(0);
    const handleincrement=()=>{
        setCount(count+1);
    }
    const handledecrement=()=>{
        setCount(count-1);
    }
    const ratingChanged=(rating)=>{
        alert(`You have Given ${rating} Stars`);
    }
    console.log(props);
    return ( <Grid item xs={4} style={{border: "inset"}}>
        <img src={product.Link} alt="Product" width="20%"></img>
        <h2>{product.name} 
        {userService.isAdmin() && 
        <>
        <Button variant="contained" color="primary" onClick={e=>{
            console.log("Update");
            history.push("/products/update/"+product._id);
        }}>   Edit</Button>
         <Button variant="contained" color="secondary" onClick={e=>{
             ProductService.deleteProduct(product._id).then((data)=>{
                 console.log(data)
                 onDelete();
             }).catch((err=>{
                 console.log(err);
             }));
         }}>   Delete</Button>
         </>}</h2>
         <section class='rating-widget'>
        
        <div class='rating-stars text-center'>
          <ul id='stars'>
            <li class='star' title='Disappointing' data-value='1'>
            <i class="fa fa-star"></i>
            </li>
            <li class='star' title='Fair' data-value='2'>
              <i class='fa fa-star fa-fw'></i>
            </li>
            <li class='star' title='satisfactory' data-value='3'>
              <i class='fa fa-star fa-fw'></i>
            </li>
            <li class='star' title='Good' data-value='4'>
              <i class='fa fa-star fa-fw'></i>
            </li>
            <li class='star' title='Excellent' data-value='5'>
              <i class='fa fa-star fa-fw'></i>
            </li>
          </ul>
        </div>
        
        <div class='success-box'>
          <div class='clearfix'></div>
          <img alt='tick image' width='32' src='data:image/svg+xml;utf8;base64,'/>
          <div class='text-message'></div>
          <div class='clearfix'></div>
        </div>
      
      </section>
        <h5>Price :</h5><p>{product.price} Pkr</p>
        <h5>Id :</h5><p>{product.pid}</p>
        <h5>Category :</h5><p>{product.category}</p>
        <h5>Details :</h5><p>{product.details}</p>
        Quantity :<Button onClick={handledecrement}><strong>-</strong></Button>
        {count}
        <Button  onClick={handleincrement}><strong>+</strong></Button>
        <br/>
        <Button variant="contained" color="secondary"><FaShopify size ="1.2rem"/>Add to Cart</Button>
        <hr/>
    </Grid> );
}
 
export default withRouter(SingleProduct);
